--
-- Table structure for table `tours`
--

CREATE TABLE IF NOT EXISTS `tours` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `image_url` text COLLATE utf8_bin,
  `event_summary` text COLLATE utf8_bin,
  `meeting_time_and_place` text COLLATE utf8_bin,
  `place_for_visit_and_tips` text COLLATE utf8_bin,
  `time_schedule` text COLLATE utf8_bin,
  `price` text COLLATE utf8_bin,
  `samurai_information` text COLLATE utf8_bin,
  `google_doc_url` text COLLATE utf8_bin,
  `inserted_on` text COLLATE utf8_bin,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=4 ;

--
-- Dumping data for table `tours`
--

INSERT INTO `tours` (`id`, `image_url`, `event_summary`, `meeting_time_and_place`, `place_for_visit_and_tips`, `time_schedule`, `price`, `samurai_information`, `google_doc_url`, `inserted_on`) VALUES
(1, 'tour_kamakura_banner.jpg', '■We will stroll around sightseeing spots in Kamakura.<br/>\n■We''ve already booked 鎌倉 古民家スタジオ・イシワタリ and we plan to hold some workshops at there.<br/>\n■Let''s have ODEN and SAKE for dinner together.', '【Kamakura Station(鎌倉駅) at 10AM】<br/>\n■Access: JR Yokosuka line(JR横須賀線)<br/>\n★If you''re going to be late or oversleep or whenever you want/need to contact us, message Emi Yamamoto on Facebook.<br/>\n★PLEASE DO NOT BE LATE for the meeting! If you will be late, you have to pay cancellation charge!', '●鎌倉 古民家スタジオ・イシワタリ<br/>\nhttp://ishiwatari.jimdo.com/ <br/>\n　<br/>\n●鎌倉大仏殿高徳院(Kōtoku-in)<br/>\nhttp://www.kotoku-in.jp/en/top.html <br/>\n　<br/>\n●鎌倉 長谷寺(Hasedera Temple)<br/>\nhttp://www.hasedera.jp/en/  <br/>\n<br/>\n●銭洗弁天(Zeniarai Benzaiten Ugafuku Shrine)<br/>\nhttp://www.japan-guide.com/e/e3111.html <br/>\n　<br/>\n●由比ケ浜(Yuigahama)<br/>\nhttp://yuigahama.sos.gr.jp/en <br/>\n', '★You can choose 華道 workhop or 和 Yoga workshop!<br/>\n　<br/>\n●If you choose "華道 workshop"<br/>\n9AM：Meetup@Kamakura Station East exit<br/>\n　　　　　　　　　↓<br/>\n9AM-2PM : Strolling around sightseeing spots in Kamakura<br/>\n(Kotoku-in, Hasedera Temple, Zeniarai Benzaiten, Yuigahama)<br/>\n　　　　　　　　　↓<br/>\n2PM-4PM：華道 workshop@Studio Ishiwatari<br/>\n　　　　　　　　　↓<br/>\n5PM-8PM：Having Oden & Sake @Studio Ishiwatari<br/>\n　<br/>\n●If you choose "和 Yoga workshop"<br/>\n9AM：Meetup@Kamakura Station East exit<br/>\n　　　　　　　　　↓<br/>\n10AM-12PM：和 Yoga workshop@Studio Ishiwatari<br/>\n　　　　　　　　　↓<br/>\n12PM-5PM：Strolling around sightseeing spots in Kamakura<br/>\n(Kotoku-in, Hasedera Temple, Zeniarai Benzaiten, Yuigahama)<br/>\n　　　　　　　　　↓<br/>\n5PM-8PM：Having Oden & Sake @Studio Ishiwatari', '★10,000yen<br/>\n●Including Dinner & Sake fee, Entrance fee, and workshop fee.<br/>\n●On-site payment', 'Coming soon...!', 'https://docs.google.com/forms/d/e/1FAIpQLSeCyLkiLVA3z86aABCz5z1kq-xHdMYCiU1AVC7nzLKOf4QZkg/viewform ', '2016-09-02 13:00:55'),
(2, 'tour_zazen_banner.jpg', '■We will try Japanese traditional activities related to ZEN ─ Shodo (Japanese calligraphy) and Zazen (Buddhism style seated meditation) at Tokyo Grand Hotel. <br/>\n■The Zen instructor will tell us how to perform Zazen and kindly tell us what the mentality of Zen is.<br/>\n■After tour, we plan to hold a lunch party so feel free to join us :D', '【JR Hamamatsucho Station(浜松町駅) South Ticket Gate(南口) at 9AM】<br/>\n●Access : JR Yamanote line(山手線), JR Keihin-Tohoku line(京浜東北線)<br/>\n★If you''re going to be late or oversleep or whenever you want/need to contact us, message Sho Sakurai on Facebook.<br/>\n★PLEASE DO NOT BE LATE for the meeting! If you will be late, you have to pay cancellation charge!', '●Tokyo Grand Hotel<br/>\nhttp://www.tokyogrand.gr.jp/eng/index.html <br/>\n　　<br/>\n●What is "Zazen"?<br/>\nhttp://global.sotozen-net.or.jp/eng/practice/zazen/howto/ <br/>\nZazen refers to a type of religious meditation performed while seated with the legs crossed and each foot resting on the opposite thigh. Zazen is practiced in Zen Buddhism.', '9AM Meetup @Hamamatsucho Station<br/>\n　　　　　　　↓<br/>\n10AM-1PM　Zen meditation practicing @Tokyo Grand Hotel　<br/>\n　　　　　　　↓<br/>\n1PM-2PM　After Lunch Party(※Optional, 2000yen per person)', '3,000 yen for participation<br/>\n※Transportation cost not included.<br/>\n※On-site payment.', '♢ 島津 清彦 ─Kiyohiko Shimazu─ ♢ <br/>\n●Carrer<br/>\n-Apr. 2012: Founder / CEO of SHIMARS co., ltd.<br/>\n-Aug. 2012: Entered the Buddhist priesthood at 曹洞宗高雲山観音寺<br/>\n-Sep. 2012: Founder / Representative Director of Zentrepreneur Association(merged with SHIMARS co., ltd.)<br/>\n-May 2014: Executive Officer of Sony Real Estate Corporation(retired) ', 'https://docs.google.com/forms/d/e/1FAIpQLSeQ848UKBTArIbem93q5vO99B_3b7javnjmOqSJAetYZmMTIg/viewform', '2016-09-02 13:17:15'),
(3, 'tour_sake_banner.jpg', 'Coming soon...!', '【沢井駅(Sawai Station) at 11:30】<br/>\n●Access：青梅線(Oume line)<br/>\n※If you''re going to be late or oversleep or whenever you want/need to contact us, message Koki Miyashita on Facebook.', 'Coming soon...!', 'Coming soon...!', '4,000 yen for participation<br/>\n※ Transportation cost not included.<br/>\n※ On-site payment.', '♢ 磯野カオリー Kaori Isonoー ♢<br/>\n●Qualifications<br/>\n2011：FBO official license of KIKISAKESHI(sake sommelier) acquired.<br/>\n2013：CAP official comrade of cheese acquired.<br/>\n2016：SSI INTERNATIONAL official license of International Sake sommelier acquired.<br/>\n　<br/>\n●Career<br/>\nMarch 2014：Finished 7th "Workshop for female entrepreneurs" at Totsuka.<br/>\nApril 2014：Attended Sake associated party.<br/>\nHosted a lecture and event of Sake.<br/>\nOpened Yokohama Osaketei, sake gift shop.<br/>\n●Osaketei Facebook page<br/>\nhttps://www.facebook.com/osaketei', 'https://docs.google.com/forms/d/e/1FAIpQLSd0JNNRxXwCmNH0tl46YJwskHhS4yCmQrocMGOl2aVV3Z2WpQ/viewform ', '2016-09-09 09:21:47');

